import React, { useEffect, useState } from "react";
import firebase from "../../../firebase";
let db = firebase.firestore();

function UserDetails({ id }) {
  const [servicedata, setservicedata] = useState([]);
  const [serviceLoading, setserviceLoading] = useState("Loading...");

  useEffect(() => {
    alert(id);
    db.collection("web_user")
      .doc(id)
      .collection("service")
      .get()
      .then((result) => {
        if (!result.empty) {
          alert("result");
          result.forEach((d) => {
            setservicedata((old) => [...old, d]);
            console.log("sdsdsd", d);
          });
        } else {
          setserviceLoading("Data Not Found");
        }
      });
    return () => {
      setserviceLoading(null);
    };
  }, [id]);
  return (
    <div>
      {servicedata.length > 0
        ? servicedata.map((d, index) => {
            return (
              <div key={index}>
                <h6>{d.data().service_name}</h6>
              </div>
            );
          })
        : serviceLoading}
    </div>
  );
}

export default UserDetails;
