import React, { useEffect, useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import firebase from "../../../firebase";
let db = firebase.firestore();
function Pation_img({ id }) {
  const [pation, setpation] = useState();
  const [chat, setchat] = useState();
  useEffect(() => {
    if (id) {
      db.collection("web_user")
        .doc(id)
        .get()
        .then((result) => {
          if (result) {
            setpation(result.data());
          }
        });
    }
    return () => {};
  }, [id]);

  useEffect(() => {
    db.collection("web_user")
      .doc(id)
      .collection("ChatRoom")
      .doc("krushnkantv1@gmail.com")
      .get()
      .then((result) => {
        if (!result.empty) {
          setchat(result.data().StartChat);
          alert(result.data().StartChat);
        }
      });
  }, [id]);

  return (
    <>
      {pation ? (
        <h6>
          <Avatar
            alt={pation.fname}
            src="/static/images/avatar/1.jpg"
            style={{ marginRight: "10px", marginTop: "5px" }}
          />
        </h6>
      ) : (
        ""
      )}
      {pation ? (
        <h6 style={{ fontWeight: "bold" }}>{`${
          pation.fname ? pation.fname : ""
        } ${pation.lname ? pation.lname : ""}`}</h6>
      ) : (
        ""
      )}
    </>
  );
}

export default Pation_img;
