    }
  };
  return (
    <div>
      <div style={{ height: "90vh", overflow: "auto" }}>
        {pation.length > 0
          ? pation.map((d, index) => {
              return (
                <Paper key={index} className="abhikeliye">
                  <List
                    hover="true"
                    onClick={() => showpation_details(d.email)}
                  >
                    <ListItem>
                      <ListItemAvatar>
                        {/* <Avatar
                          alt="Remy Sharp"
                          src="/static/images/avatar/1.jpg"
                        /> */}
                        <Avatar
                          alt={d.fname}
                          src="/static/images/avatar/1.jpg"
                        />
                      </ListItemAvatar>
                      <ListItemText
                        primary={`${d.fname ? d.fname : ""} ${
                          d.lname ? d.lname : ""
                        }`}
                      />
                    </ListItem>
                  </List>
                </Paper>
              );
            })
          : "...Loading"}
      </div>
    </div>
  );
}

export default Pation;
