import React, { useEffect, useState } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import firebase from "../../../firebase";
import { useDispatch } from "react-redux";
import { Chat } from "../action/Action";

let db = firebase.firestore();
function Pation_img({ id }) {
  let dispatch = useDispatch();
  const [pation, setpation] = useState();
  const [chat, setchat] = useState();
  useEffect(() => {
    if (id) {
      db.collection("web_user")
        .doc(id)
        .get()
        .then((result) => {
          if (result) {
            setpation(result.data());
          }
        });
    }
    return () => {};
  }, [id]);

  useEffect(() => {
    db.collection("web_user")
      .doc(id)
      .collection("ChatRoom")
      .where("Docter_email", "==", "krushnkantv1@gmail.com")
      .limit(1)
      .onSnapshot((result) => {
        if (!result.empty) {
          result.forEach((d) => {
            setchat(d.data().StartChat);
            console.log("result_Data", d.data());
          });
        } else {
          db.collection("web_user")
            .doc(id)
            .collection("ChatRoom")
            .doc("krushnkantv1@gmail.com")
            .set({ StartChat: "stop", Docter_email: "krushnkantv1@gmail.com" });
        }
      });
  }, [id]);

  return (
    <>
      {pation ? (
        <h6>
          <Avatar
            alt={pation.fname}
            src="/static/images/avatar/1.jpg"
            style={{ marginRight: "10px", marginTop: "5px" }}
          />
        </h6>
      ) : (
        ""
      )}
      {pation ? (
        <h6 style={{ fontWeight: "bold" }}>{`${
          pation.fname ? pation.fname : ""
        } ${pation.lname ? pation.lname : ""}`}</h6>
      ) : (
        ""
      )}
      {chat ? (
        <h6>
          <Button
            style={{ position: "absolute", right: "10px", top: "5px" }}
            variant="contained"
            color="primary"
          >
            {chat}
          </Button>
        </h6>
      ) : (
        ""
      )}
    </>
  );
}

export default Pation_img;
