import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";

import Paper from "@material-ui/core/Paper";

import firebase from "../../../firebase";
import "./Doctor.css";
let db = firebase.firestore();
//
const useStyles = makeStyles((theme) => ({
  root: {
    width: "90%",
    marginLeft: "auto",
    marginRight: "auto",
    marginTop: "10px",
    backgroundColor: theme.palette.background.paper,
    cursor: "pointer",
  },
  paper: {
    textAlign: "center",
    backgroundColor: "#ecf4fd",
  },
  hover: {
    backgroundColor: "red",
  },
}));
//
function Pation({ setpation_name }) {
  const classes = useStyles();
  const [doctor, setdoctor] = useState([]);
  const [pation, setpation] = useState([]);
  useEffect(() => {
    db.collection("web_user")
      .get()
      .then((result) => {
        if (result) {
          result.forEach((data) => {
            setpation((old) => [...old, data.data()]);
          });
        }
      });
  }, [setpation_name]);
  let showdocter_chat = (docter_name) => {
    if (docter_name) {
      setpation_name(docter_name);
    }
  };
  return (
    <div>
      {pation.length > 0
        ? pation.map((d, index) => {
            return (
              <Paper key={index} className="abhikeliye">
                <List hover="true" onClick={() => showdocter_chat(d.User)}>
                  <ListItem>
                    <ListItemAvatar>
                      <Avatar alt={d.User} src={d.User_Img} />
                    </ListItemAvatar>
                    <ListItemText primary={d.User_Name} />
                  </ListItem>
                </List>
              </Paper>
            );
          })
        : "...Loading"}
    </div>
  );
}

export default Pation;
